        list2[0] = RLPWriter.writeString("asdf");
        list2[1] = RLPWriter.writeString("qwer");
        list2[2] = RLPWriter.writeString("zxcv");

        for (uint256 i = 0; i < 32; i++) {
            list[i] = RLPWriter.writeList(list2);
        }

        assertEq(
            RLPWriter.writeList(list),
