        );
    }

    function test_writeList_listoflists_succeeds() external {
        // [ [ [], [] ], [] ]
        bytes[] memory list = new bytes[](2);
        bytes[] memory list2 = new bytes[](2);

        list2[0] = RLPWriter.writeList(new bytes[](0));
